$(function() {

  $("#campaignform input,#campaignform textarea").jqBootstrapValidation({
    preventSubmit: true,
    submitError: function($form, event, errors) {
      // additional error messages or events
    },
    submitSuccess: function($form, event) {
      event.preventDefault(); // prevent default submit behaviour
      // get values from FORM
      var country = $("input#country").val();
      var campaign_name = $("input#campaign_name").val();
		var start_date = $("input#start_date").val();
		var end_date = $("input#end_date").val();
		var minimum_target_amount = $("input#minimum_target_amount").val();
      var maximum_payout = $("input#maximum_payout").val();
		 // For Success/Failure Message
      // Check for white space in name for Success/Fail message
      if (firstName.indexOf(' ') >= 0) {
        firstName = name.split(' ').slice(0, -1).join(' ');
      }
      $this = $("#sendMessageButton");
      $this.prop("disabled", true); // Disable submit button until AJAX call is complete to prevent duplicate messages
      $.ajax({
			url: "././mail/contact_me.php",
			type: "POST",
			data: {
				country: country,          
				campaign_name: campaign_name,
				start_date: start_date,
				end_date:end_date,
				minimum_target_amount:minimum_target_amount,
				
				maximum_payout:maximum_payout,
			},
			cache: false,
			success: function() {
				// Success message
				/* $('#success').html("<div class='alert alert-success'>");
				$('#success > .alert-success').html("<button type='button' class='close' data-dismiss='alert' aria-hidden='true'>&times;")
				.append("</button>");
				$('#success > .alert-success')
				.append("<strong>Your message has been sent. </strong>");
				$('#success > .alert-success')
				.append('</div>'); */
				//clear all fields
				$('#b2cmodel').modal('toggle');				
				setTimeout(function() {
					window.location.replace("index.html");
				}, 5000);
				$('#campaignform').trigger("reset");
			},
			error: function() {
				// Fail message
				$('#success').html("<div class='alert alert-danger'>");
				$('#success > .alert-danger').html("<button type='button' class='close' data-dismiss='alert' aria-hidden='true'>&times;")
				.append("</button>");
				$('#success > .alert-danger').append($("<strong>").text("Sorry " + firstName + ", it seems that my mail server is not responding. Please try again later!"));
				$('#success > .alert-danger').append('</div>');
				//clear all fields
				$('#campaignform').trigger("reset");
			},
			complete: function() {
				setTimeout(function() {
					$this.prop("disabled", false); // Re-enable submit button when AJAX call is complete
				}, 1000);
			}
      });
    },
    filter: function() {
      return $(this).is(":visible");
    },
  });

  $("a[data-toggle=\"tab\"]").click(function(e) {
    e.preventDefault();
    $(this).tab("show");
  });
});

/*When clicking on Full hide fail/success boxes */
$('#name').focus(function() {
  $('#success').html('');
});
